package operadores;

public class Asignacion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Operadores += , -=
		int n1 = 10;
		//Sumarle 10 a n1
		
		n1+=10; //n1 = n1 + 10;
		
		n1-=10; //n1 = n1 - 10;
		n1*=10; //n1 = n1 * 10;
		n1/=10; //n1 = n1 / 10;
		
		
		
	}

}
